import React from "react";
import "./styles.css";
import { Spring } from "react-spring/renderprops";
import HamburgerMenu from "react-hamburger-menu";
import { Link } from "react-scroll";

class NavBarHam extends React.Component {
  constructor() {
    super();
    this.state = {
        isVisibile: false,
      open: false,
      showDropDown: false,
      width: window.innerWidth,
    };
  }

  componentDidMount() {
    window.addEventListener("resize", this.updateWidth);
  }
  componentWillUnmount() {
    window.addEventListener("resize", this.updateWidth);
  }
  updateWidth = () => {
    this.setState({
      width: window.innerWidth,
    });
  };

  handleClick = () => {
    this.setState({
      open: !this.state.open,
      showDropDown: !this.state.showDropDown,
    });
  };

  render() {
    // console.log(this.state.isVisibile);
    // console.log(document.body.getBoundingClientRect().top);
    return (
      <Spring
        from={{ opacity: 0 }}
        to={{ opacity: 1 }}
        config={{ delay: 1400, duration: 3000 }}
      >
        {(props) => (
          <div style={{ position: "absolute" }}>
            <div
              className="MyNav"
              style={{
                paddingLeft: this.state.isVisibile ? "1%" : "10%",
                paddingRight: this.state.isVisibile ? "1%" : "10%",
                backgroundColor: this.state.isVisibile ? "#193964" : "#A3CEEE",
                webkitTransition: "all 0.6s ease-out",
                mozTransition: "all 0.6s ease-out",
                oTransition: "all 0.6s ease-out",
                msTransition: "all 0.6s ease-out",
                transition: "all 0.6s ease-out",
              }}
            >
              <div>
                {/* TODO: find a better way to refresh the page  */}
                <a href="JavaScript: location.reload(true);">
                  <img
                    alt="logo"
                    className="DbgLogo"
                    src={require("../Icons/logo white.png")}
                  />
                </a>
              </div>

              <HamburgerMenu
                isOpen={this.state.open}
                menuClicked={this.handleClick.bind(this)}
                width={18}
                height={15}
                strokeWidth={1}
                rotate={0}
                color="black"
                borderRadius={0}
                animationDuration={0.5}
                style={{ zIndex: "20" }}
              />
            </div>

            <div
              className="NavHamDropdown"
              style={{
                width: this.state.width,
                marginTop: "85px",
                padding: "15px",
                position: "fixed",
                visibility: this.state.showDropDown ? "visible" : "hidden",
              }}
            >
              <Link
                activeClass="active"
                to="about"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                    //   margin: "auto",
                    //   alignItems: "center",
                    //   resize: "both",
                    //   paddingTop: "0px !important",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">About</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="skills"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Skills</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="experience"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Experience</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="work"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Work/Projects</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="about"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Connect</div>
                </div>
              </Link>
            </div>
          </div>
        )}
      </Spring>
    );
  }
}

export default NavBarHam;
