import React from "react";
import "./styles.css";
import { Spring } from "react-spring/renderprops";
// import SocialItems from "./SocialItems";
import NavBarCapsules from "./NavBarCapsules";
import Capsule from "./Capsule";
import MediaQuery from "react-responsive";

class NavBar extends React.Component {
  constructor() {
    super();
    this.state = {
      isVisibile: false,
    };
  }

  componentDidMount() {
    window.addEventListener("scroll", this.handlecroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handlecroll);
  }

  handlecroll = () => {
    this.setState({
      isVisibile: document.body.getBoundingClientRect().top < -328,
    });
  };

  render() {
    return (
      <Spring
        from={{ opacity: 0 }}
        to={{ opacity: 1 }}
        config={{ delay: 1400, duration: 3000 }}
      >
        {(props) => (
          <div className="MyNav">
            <div>
              <a href="JavaScript: location.reload(true);">

                <img
                  className="DbgLogo "
                  src={require("../Icons/logo white.png")}
                />
              </a>
            </div>

            <div
              className="SocialWrapper"
              style={{
                visibility: this.state.isVisibile ? "visible" : "hidden",
              }}
            >
              <div
                className="SocialWrapper"
                style={{
                  position: "absolute",
                }}
              >
                <div className="NavBarCapsulesSticky">
                  <NavBarCapsules />
                </div>
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                  }}
                >
                  <Capsule
                    name="Get in touch"
                    from={{ opacity: 0 }}
                    to={{ opacity: 1 }}
                    config={{ delay: 6200, duration: 500 }}
                  />
                </div>
              </div>

              <div
                className="SocialWrapper"
                style={{
                  visibility: this.state.isVisibile ? "hidden" : "visible",
                }}
              >
                <div
                //   style={{
                //     fontFamily: "papyrus",
                //     fontWeight: "bold",
                //     fontSize: "20px",
                //   }}
                >
                  <div
                    className="Connect"
                    style={
                      {
                        //   alignSelf: "center",
                        //   borderTopLeftRadius: "10px",
                        //   borderTopRightRadius: "10px",
                        //   borderBottomRightRadius: "10px",
                        //   background: "#EA5D5A",
                        //   padding: "8px",
                        //   width: "160px",
                        //   color: "white",
                        //   textAlign: "center",
                        //   marginBottom: "5px",
                        //   transition: "transform 0.5s",
                        //   fontWeight: "bold",
                        //   fontSize: "20px",
                        //   fontFamily: "playfair display"
                      }
                    }
                  >
                    Get in touch
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </Spring>
    );
  }
}

export default NavBar;
