import React from "react";
import "./styles.css";
import { Spring } from "react-spring/renderprops";
import HamburgerMenu from "react-hamburger-menu";
import { Link } from "react-scroll";

class NavBarHam extends React.Component {
  constructor() {
    super();
    this.state = {
      changeNavColor: false,
      open: false,
      showDropDown: false,
      width: window.innerWidth,
    };
  }

  componentDidMount() {
    window.addEventListener("resize", this.updateWidth);
    window.addEventListener("scroll", this.handlecroll);
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.updateWidth);
    window.removeEventListener("scroll", this.handlecroll);
  }
  updateWidth = () => {
    this.setState({
      width: window.innerWidth,
    });
  };
  handlecroll = () => {
    this.setState({
      changeNavColor: document.body.getBoundingClientRect().top < -590,
    });
  };

  handleClick = () => {
    this.setState({
      open: !this.state.open,
      showDropDown: !this.state.showDropDown,
    });
  };

  render() {
    console.log(this.state.open);
    // console.log(document.body.getBoundingClientRect().top);
    return (
      <Spring
        from={{ opacity: 0 }}
        to={{ opacity: 1 }}
        config={{ delay: 1400, duration: 3000 }}
      >
        {(props) => (
          <div style={{ position: "absolute" }}>
            <div
              className="MyNav"
              style={{
                // paddingLeft: this.state.isVisibile ? "1%" : "10%",
                // paddingRight: this.state.isVisibile ? "1%" : "10%",
                backgroundColor: this.state.changeNavColor
                  ? "#193964"
                  : "#A3CEEE",
                webkitTransition: "all 0.6s ease-out",
                mozTransition: "all 0.6s ease-out",
                oTransition: "all 0.6s ease-out",
                msTransition: "all 0.6s ease-out",
                transition: "all 0.6s ease-out",
              }}
            >
              <div>
                {/* TODO: find a better way to refresh the page  */}
                <a href="JavaScript: location.reload(true);">
                  <img
                    alt="logo"
                    className="DbgLogo"
                    src={require("../Icons/logo white.png")}
                  />
                </a>
              </div>
              <div
                style={{
                  marginRight: this.state.open ? "250px" : "0px",
                }}
              >
                <HamburgerMenu
                  isOpen={this.state.open}
                  menuClicked={this.handleClick.bind(this)}
                  width={18}
                  height={15}
                  strokeWidth={1}
                  rotate={0}
                  color={this.state.changeNavColor ? "white" : "black"}
                  borderRadius={0}
                  animationDuration={0.5}
                />
              </div>
            </div>

            <div
              className="NavHamDropdown"
              style={{
                // width: this.state.width,
                marginLeft: this.state.width - 350,
                padding: "15px",
                position: "fixed",
                visibility: this.state.showDropDown ? "visible" : "hidden",
                backgroundColor: this.state.changeNavColor
                  ? "#193964"
                  : "#A3CEEE",
              }}
            >
              <Link
                activeClass="active"
                to="about"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                    //   margin: "auto",
                    //   alignItems: "center",
                    //   resize: "both",
                    //   paddingTop: "0px !important",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">About</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="skills"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Skills</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="experience"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Experience</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="work"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Work/Projects</div>
                </div>
              </Link>
              <Link
                activeClass="active"
                to="about"
                spy={true}
                smooth={true}
                offset={-100}
                duration={1200}
              >
                <div
                  style={{
                    fontFamily: "papyrus",
                    fontWeight: "bold",
                    fontSize: "20px",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <div className="shadow rounded mb-4 Tabs">Connect</div>
                </div>
              </Link>
            </div>
          </div>
        )}
      </Spring>
    );
  }
}

export default NavBarHam;
