import React from "react";
import MyImg from "../Icons/img-devanshiProfile.png";
import Capsule from "./Capsule";

function About() {
  return (
    <div id="about" style={MainDiv} data-aos="fade-in" data-aos-duration="9000">
      <div style={Heading}>
        <div>About Me</div>
      </div>
      <div
        className="container shadow p-3 mb-5 rounded"
        style={{
          backgroundColor: "#F7F7F7",
          padding: "1%",
          borderRadius: "10px",
        }}
      >
        <div className="row" style={ImageAndBody}>
          <div className="col-lg-5 col-md-5 col-sm-12">
            <img alt="" style={Img} src={MyImg} />
          </div>
          <div className="col-lg-4 ol-md-5 col-sm-12" style={Content}>
            <span>
              Hey there! <br />
              This is Devanshi here. <br />
            </span>
            <span>
              <br />I am a Computer Science graduate student at Northeastern
              Univeristy. I enjoy anything that involves inventiveness, novelty
              or transforming imagination to reality; hence I closely associate
              myself with web development and love creating new things on the
              web.
            </span>
            <span>
              <br />
              <br />
              <br />
              <Capsule
                name="Grab my resume"
                from={{ opacity: 0 }}
                to={{ opacity: 1 }}
                config={{ delay: 6200, duration: 500 }}
              />
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

const MainDiv = {
  marginTop: "70px",
  // height: window.innerHeight,
};

const Heading = {
  justifyContent: "center",
  display: "flex",
  fontWeight: "bold",
  fontSize: "50px",
  fontFamily: "papyrus",
  color: "rgb(139, 173, 218)",
  padding: "3%",
};

const Img = {
  borderRadius: "10px",
  width: "380px",
  height: "500px",
  margin: "1%",
  display: "inline-block",
};

const Content = {
  fontWeight: "bold",
  fontSize: "22px",
  fontFamily: "papyrus",
  color: "rgb(68, 68, 68)",
  margin: "1%",
  textAlign: "left",
  display: "inline-block",
};

const ImageAndBody = {
  display: "flex",
  justifyContent: "center",
  textAlign: "center",
};

export default About;
