import React from "react";

class ProjectCard extends React.Component {
    constructor(){
        super();
    }

    openSlider = () => {
        document.getElementById("onTop").classList.add("ProjectCardOverlayVisible");
      };

      closeSlider = () => {
        document
          .getElementById("onTop")
          .classList.remove("ProjectCardOverlayVisible");
      };

    render(){
        return(
        <div
            className="card border-0 ProjectCard ml-2 mr-2 mt-2 mb-2"
            data-aos="zoom-in"
            data-aos-duration="10000"
            // style={{ backgroundColor: "rgb(40, 69, 111)" }}
            // style={{ backgroundColor: "rgb(23, 51, 90)" }}
          >
            <div className="ProjectNameLighter">
              <div>Pro-Manager</div>
            </div>
            <div id="onTop" className="ProjectCardOverlay">
              <div className="ProjectCardText">
                <div>
                Hello World
                </div>
                {/* <p></p> */}
                <div style={{ display:"flex", flexDirection:"row", alignItems:"center", justifyContent:"center"}}>
                {/* <button
                  className="btn-sm "
                  style={Button}
                  onClick={() => this.closeSlider()}
                >
                  shut me close
                </button> */}
                <img
                  alt="close"
                  src={require("../Icons/Icons-10.png")}
                  style={{ maxWidth: "40%", maxHeight: "40%" }}
                  onClick={() => this.closeSlider()}
                />
                </div>
                {/* <p></p> */}
              </div>
            </div>
            <div className="ProjectContentLighter p-4">
              <div style={{ fontSize: "18px", alignSelf:"start" }}>
                <div>
                  <ul
                    style={{
                      listStyleType: "none",
                      // borderBottom: "1px",
                      textAlign: "start",
                      padding: "0.5rem",
                    }}
                  >
                    <li
                      style={{
                        // borderBottom: "1px solid #eee",
                        padding: "0.5rem",
                      }}
                    >
                      react | Redux
                    </li>
                    <li
                      style={{
                        // borderBottom: "1px solid #eee",
                        padding: "0.5rem",
                      }}
                    >
                      Heroku
                    </li>
                    <li
                      style={{
                        // borderBottom: "1px solid #eee",
                        padding: "0.5rem",
                      }}
                    >
                      MySQL
                    </li>
                  </ul>
                </div>
              </div>
              <div style={{ display:"flex", flexDirection:"row", alignItems:"center", justifyContent:"space-around", paddingBottom: "15px" }}>
                {/* <button
                  type="button"
                  style={Button}
                  className="btn-sm shadow rounded"
                  onClick={() => this.openSlider()}
                >
                  Learn more
                </button> */}
                                    <img
                  alt="open"
                  src={require("../Icons/Icons-15.png")}
                  style={{ maxWidth: "30%", maxHeight: "30%" }}
                  onClick={() => this.openSlider()}
                />
                <img alt="" src={require("../Icons/Icons-09.png")} style={{maxWidth: "50%", maxHeight: "50%"}} />
              </div>
            </div>
            </div>);
    }
}

export default ProjectCard;
